<?php require_once '../../../assets/db_connect.php';if($_POST) {		$validator = array('success' => false, 'messages' => array());	$idptk=$_POST['idptk'];	$idp=$_POST['idp'];	$status=$_POST['status'];	$sql = "select * from siswa where peserta_didik_id='$idptk'";		$query = $connect->query($sql);		$cks = $query->fetch_assoc();		$ada=$query->num_rows;		if($ada>0){			$sql1 = "UPDATE siswa SET status='$status' WHERE peserta_didik_id='$idptk'";			$query1 = $connect->query($sql1);			if($query1 === TRUE) {							$validator['success'] = true;				$validator['messages'] = "Status ".$cks['nama']." Berhasil diubah!";					} else {						$validator['success'] = false;				$validator['messages'] = "Error while adding the member information";			};		}else{			$validator['success'] = false;			$validator['messages'] = "Siswa tidak diketemukan";		};	$connect->close();	echo json_encode($validator);}